import React, { useState } from "react"
import PropTypes from 'prop-types';
import { Icon } from '@iconify/react';
import searchFill from '@iconify/icons-eva/search-fill';
import trash2Fill from '@iconify/icons-eva/trash-2-fill';
import roundFilterList from '@iconify/icons-ic/round-filter-list';
// material
import { styled } from '@mui/material/styles';
import {
  Box,
  Toolbar,
  Tooltip,
  IconButton,
  Typography,
  OutlinedInput,
  InputAdornment
} from '@mui/material';
import axios from 'axios'
import { API_URL, DELETEALLGALLRYIMAGE } from "../../../../Apiconstant/Apiconstant"
// ----------------------------------------------------------------------
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link as RouterLink, useNavigate } from 'react-router-dom';

const RootStyle = styled(Toolbar)(({ theme }) => ({
  height: 96,
  display: 'flex',
  justifyContent: 'space-between',
  padding: theme.spacing(0, 1, 0, 3)
}));

const SearchStyle = styled(OutlinedInput)(({ theme }) => ({
  width: 240,
  transition: theme.transitions.create(['box-shadow', 'width'], {
    easing: theme.transitions.easing.easeInOut,
    duration: theme.transitions.duration.shorter
  }),
  '&.Mui-focused': { width: 320, boxShadow: theme.customShadows.z8 },
  '& fieldset': {
    borderWidth: `1px !important`,
    borderColor: `${theme.palette.grey[500_32]} !important`
  }
}));

// ----------------------------------------------------------------------

UserListToolbar.propTypes = {
  numSelected: PropTypes.number,
  filterName: PropTypes.string,
  onFilterName: PropTypes.func
};

export default function UserListToolbar({ numSelected, filterName, onFilterName, selected }) {
  const navigate = useNavigate();
  const [response, setresponse] = useState()
  const deleteDonation = () => {
    const success = () => { }

    const durl = `${API_URL}/${DELETEALLGALLRYIMAGE}`;
    axios.post(durl, {
      noOfUser: selected
    }, {
      headers: {
        'x-token': localStorage.getItem('id_token'),
      }
    })
      .then((response) => {

        if (response.status === 200) {
          setresponse(response.data)
          success(toast.success(response.data.message))
        }
        navigate('/dashboard/gallery-image', { state: { status: 'done' } });

      })
  };
  return (
    <>
    {(response) ?
      <>
      </>
      :
      <>
        <RootStyle
          sx={{
            ...(numSelected > 0 && {
              color: 'primary.main',
              bgcolor: 'primary.lighter'
            })
          }}
        >
          {numSelected > 0 ? (
            <Typography component="div" variant="subtitle1">
              {numSelected} selected
            </Typography>
          ) : (
            <SearchStyle
              value={filterName}
              onChange={onFilterName}
              placeholder="Search user..."
              startAdornment={
                <InputAdornment position="start">
                  <Box component={Icon} icon={searchFill} sx={{ color: 'text.disabled' }} />
                </InputAdornment>
              }
            />
          )}
          {numSelected > 0 ? (
            <Tooltip title="Delete">
              <IconButton>

                <Icon icon={trash2Fill} onClick={() => deleteDonation()} />
              </IconButton>


            </Tooltip>
          ) : (
            <>
            </>
          )}
           </RootStyle >
        </>

      }
    </>





  );
}
